// Step 1: Extract the raw string from AI output
let raw = $json.output || $json.text || $json.data || JSON.stringify($json);

// Step 2: Find and extract only the JSON portion
const firstBrace = raw.indexOf('{');
const lastBrace = raw.lastIndexOf('}');
if (firstBrace === -1 || lastBrace === -1) {
  return [{ json: { error: 'No JSON block found in AI output', raw_output: raw } }];
}
const jsonString = raw.slice(firstBrace, lastBrace + 1);

// Step 3: Parse the extracted JSON safely
let parsed;
try {
  parsed = JSON.parse(jsonString);
} catch (err) {
  return [{ json: { error: 'Invalid JSON format', details: err.message, raw_json: jsonString } }];
}

// Step 4: Extract the fields
const website = parsed.website || {};
const topicRaw = parsed.topic || '';
const title = website.title || parsed.title || 'Unnamed Website';
const sections = (website.sections || []).map(s => s.name || '').filter(Boolean);

// Step 5: Normalize the topic
let website_type = topicRaw.toLowerCase();
if (website_type.includes('hospital') || website_type.includes('healthcare')) website_type = 'hospital';
else if (website_type.includes('education')) website_type = 'education';
else if (website_type.includes('restaurant')) website_type = 'restaurant';
else website_type = 'business';

// Step 6: Build final Website API payload
const result = {
  website_type,
  business_name: title,
  sections_required: sections
};

// Step 7: Return
return [{ json: result }];

