// Handle nested response arrays safely
let data;

// Detect different nesting formats
if (Array.isArray($json) && Array.isArray($json[0])) {
  // Double nested: [ [ {...} ] ]
  data = $json[0][0];
} else if (Array.isArray($json) && typeof $json[0] === 'object') {
  // Single nested: [ {...} ]
  data = $json[0];
} else {
  // Flat object
  data = $json;
}

// Extract arrays
const imageRequests = data.images_needed || [];
const voiceScripts = data.voice_scripts_needed || [];

// Prepare email subject and body
let subject = "Connection Team ‚Äì Website Asset Requirements";
let body = "";

// üñºÔ∏è Section 1: Image Requests
if (imageRequests.length > 0) {
  const imageList = imageRequests
    .map((req, i) => `${i + 1}. Section: ${req.id}\n   Description: ${req.description}`)
    .join('\n\n');
  body += `The website team requires the following images:\n\n${imageList}\n\n`;
} else {
  body += "No image requests received from the website team.\n\n";
}

// üéôÔ∏è Section 2: Voice Scripts
if (voiceScripts.length > 0) {
  body += "Voice scripts required for the website sections:\n\n";
  const scriptList = voiceScripts
    .map(v => `Section: ${v.id}\nScript: ${v.script}`)
    .join('\n\n');
  body += `${scriptList}\n\n`;
} else {
  body += "No voice scripts received from the website team.\n\n";
}

// ‚úâÔ∏è Add footer
body += "Please prepare and send the requested assets to the design team.";

// ‚úÖ Always return a valid n8n JSON object
return [
  {
    json: {
      subject,
      body
    }
  }
];
